SELECT COUNT(*) FROM zafira.TEST_RUNS TR LEFT JOIN zafira.USERS U ON TR.USER_ID = U.ID LEFT JOIN zafira.JOBS J ON TR.JOB_ID = J.ID LEFT JOIN zafira.TEST_SUITES TS ON TR.TEST_SUITE_ID = TS.ID
AND TR.ID = #{id}
AND LOWER(TS.NAME) LIKE LOWER(CONCAT('%', #{testSuite}, '%'))
AND TS.ID = #{testSuiteId}
AND CONCAT(J.JOB_URL, '/', TR.BUILD_NUMBER) LIKE CONCAT('%', #{executionURL}, '%')
AND TR.ENV = #{environment}
AND TR.PLATFORM = #{platform}
AND LOWER(TR.APP_VERSION) LIKE LOWER(CONCAT('%', #{appVersion}, '%'))
AND TR.STARTED_AT BETWEEN #{date}::timestamp AND #{date}::timestamp + '24:00:00'::interval
AND TR.CREATED_AT::date >= #{fromDate}::date
AND TR.CREATED_AT::date <= #{toDate}::date
AND TR.PROJECT_ID = #{project.id}
AND TR.STATUS = #{status}

